import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { insertLeaveRequestSchema } from "@shared/schema";
import { apiRequest } from "@/lib/queryClient";
import { useAuth } from "@/contexts/AuthContext";
import { useToast } from "@/hooks/use-toast";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Checkbox } from "@/components/ui/checkbox";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { z } from "zod";

const formSchema = insertLeaveRequestSchema;

type FormData = z.infer<typeof formSchema>;

export default function LeaveApplicationForm() {
  const { userData } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const form = useForm<FormData>({
    resolver: zodResolver(formSchema),
    defaultValues: {
      student_id: userData?.id || "",
      leave_type: "",
      reason: "",
      start_date: "",
      end_date: "",
      guardian_phone: "",
      emergency_contact: "",
      supporting_docs: "",
    },
  });

  const submitMutation = useMutation({
    mutationFn: async (data: FormData) => {
      const response = await apiRequest("POST", "/api/leave-requests", data);
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Leave application submitted successfully",
      });
      form.reset();
      queryClient.invalidateQueries({ queryKey: ["/api/leave-requests"] });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to submit leave application",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: FormData) => {
    // Add hostel status from student profile
    const isHostelStudent = userData?.hostel_status === "Hostel";
    const submitData = {
      ...data,
      is_hostel_student: isHostelStudent
    };
    submitMutation.mutate(submitData);
  };

  return (
    <Card>
      <CardHeader>
        <CardTitle>Apply for Leave</CardTitle>
        <CardDescription>Fill out the form below to submit a new leave request</CardDescription>
      </CardHeader>
      <CardContent>
        <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
          {/* Leave Type */}
          <div className="space-y-2">
            <Label htmlFor="leaveType">Leave Type</Label>
            <Select 
              value={form.watch("leave_type")} 
              onValueChange={(value) => form.setValue("leave_type", value)}
            >
              <SelectTrigger data-testid="select-leave-type">
                <SelectValue placeholder="Select leave type" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="medical">Medical Leave</SelectItem>
                <SelectItem value="personal">Personal Leave</SelectItem>
                <SelectItem value="emergency">Emergency Leave</SelectItem>
                <SelectItem value="family">Family Function</SelectItem>
              </SelectContent>
            </Select>
            {form.formState.errors.leave_type && (
              <p className="text-sm text-destructive">{form.formState.errors.leave_type.message}</p>
            )}
          </div>

          {/* Date Range */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label htmlFor="start_date">From Date</Label>
              <Input
                id="start_date"
                type="date"
                {...form.register("start_date")}
                data-testid="input-from-date"
              />
              {form.formState.errors.start_date && (
                <p className="text-sm text-destructive">{form.formState.errors.start_date.message}</p>
              )}
            </div>
            <div className="space-y-2">
              <Label htmlFor="end_date">To Date</Label>
              <Input
                id="end_date"
                type="date"
                {...form.register("end_date")}
                data-testid="input-to-date"
              />
              {form.formState.errors.end_date && (
                <p className="text-sm text-destructive">{form.formState.errors.end_date.message}</p>
              )}
            </div>
          </div>

          {/* Reason */}
          <div className="space-y-2">
            <Label htmlFor="reason">Reason for Leave</Label>
            <Textarea
              id="reason"
              rows={4}
              placeholder="Provide detailed reason for your leave request..."
              {...form.register("reason")}
              data-testid="textarea-reason"
            />
            {form.formState.errors.reason && (
              <p className="text-sm text-destructive">{form.formState.errors.reason.message}</p>
            )}
          </div>

          {/* Guardian Details */}
          <div className="space-y-2">
            <Label htmlFor="guardian_phone">Guardian Phone Number *</Label>
            <Input
              id="guardian_phone"
              type="tel"
              placeholder="+91 9876543210"
              {...form.register("guardian_phone")}
              data-testid="input-guardian-phone"
            />
            {form.formState.errors.guardian_phone && (
              <p className="text-sm text-destructive">{form.formState.errors.guardian_phone.message}</p>
            )}
          </div>

          {/* Display hostel status from profile */}
          {userData?.hostel_status && (
            <div className="bg-muted p-3 rounded-md">
              <p className="text-sm text-muted-foreground">
                Hostel Status: <span className="font-medium text-foreground">{userData.hostel_status}</span>
              </p>
            </div>
          )}

          {/* Emergency Contact */}
          <div className="space-y-2">
            <Label htmlFor="emergency_contact">Emergency Contact Number</Label>
            <Input
              id="emergency_contact"
              type="tel"
              placeholder="+91 9876543210"
              {...form.register("emergency_contact")}
              data-testid="input-emergency-contact"
            />
          </div>

          {/* Submit Button */}
          <Button 
            type="submit" 
            className="w-full" 
            disabled={submitMutation.isPending}
            data-testid="button-submit-leave"
          >
            {submitMutation.isPending ? "Submitting..." : "Submit Leave Application"}
          </Button>
        </form>
      </CardContent>
    </Card>
  );
}

client/src/pages/applications.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { useAuth } from "@/contexts/AuthContext";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Loader2, FileText, Calendar, Clock, CheckCircle, XCircle, AlertCircle, QrCode, Download } from "lucide-react";
import Sidebar from "@/components/Sidebar";
import type { LeaveRequest } from "@shared/schema";

function getStatusVariant(status: string) {
  switch (status.toLowerCase()) {
    case "approved":
      return "default" as const;
    case "rejected":
      return "destructive" as const;
    case "pending":
      return "secondary" as const;
    default:
      return "secondary" as const;
  }
}

function getStatusIcon(status: string) {
  switch (status.toLowerCase()) {
    case "approved":
      return <CheckCircle className="h-4 w-4" />;
    case "rejected":
      return <XCircle className="h-4 w-4" />;
    case "pending":
      return <Clock className="h-4 w-4" />;
    default:
      return <AlertCircle className="h-4 w-4" />;
  }
}

export default function Applications() {
  const { userData } = useAuth();

  const { data: applications = [], isLoading } = useQuery<LeaveRequest[]>({
    queryKey: ["/api/leave-requests/student", userData?.id],
    enabled: !!userData?.id,
  });

  const { data: currentApplication } = useQuery<LeaveRequest | null>({
    queryKey: ["/api/leave-requests/current", userData?.id],
    enabled: !!userData?.id,
  });

  const handleDownloadPDF = async (applicationId: string) => {
    try {
      const response = await fetch(`/api/leave-requests/${applicationId}/pdf`);
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `leave-pass-${applicationId}.pdf`;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);
    } catch (error) {
      console.error("Error downloading PDF:", error);
    }
  };

  if (isLoading) {
    return (
      <div className="flex h-screen bg-background">
        <Sidebar />
        <div className="flex-1 ml-64 flex items-center justify-center">
          <div className="flex items-center space-x-2">
            <Loader2 className="h-6 w-6 animate-spin" />
            <span>Loading applications...</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="flex min-h-screen bg-background">
      <Sidebar />
      <div className="flex-1 ml-64 p-8">
        <div className="max-w-6xl mx-auto">
          {/* Header */}
          <div className="flex items-center justify-between mb-8">
            <div>
              <h1 className="text-3xl font-bold text-foreground" data-testid="heading-applications">My Applications</h1>
              <p className="text-muted-foreground mt-2">View and track all your leave applications</p>
            </div>
            <div className="flex items-center space-x-2 text-sm text-muted-foreground">
              <FileText className="h-4 w-4" />
              <span>{applications.length} Total Applications</span>
            </div>
          </div>

          {/* Current Application Alert */}
          {currentApplication && (
            <Card className="mb-6 border-primary/20 bg-primary/5">
              <CardHeader className="pb-3">
                <CardTitle className="text-lg flex items-center space-x-2">
                  <AlertCircle className="h-5 w-5 text-primary" />
                  <span>Current Active Application</span>
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
                  <div>
                    <p className="text-sm text-muted-foreground">Application ID</p>
                    <p className="font-medium">#{currentApplication.id?.slice(-6)}</p>
                  </div>
                  <div>
                    <p className="text-sm text-muted-foreground">Leave Type</p>
                    <p className="font-medium">{currentApplication.leave_type}</p>
                  </div>
                  <div>
                    <p className="text-sm text-muted-foreground">Status</p>
                    <Badge variant={getStatusVariant(currentApplication.status)} className="mt-1">
                      {getStatusIcon(currentApplication.status)}
                      <span className="ml-1">{currentApplication.status}</span>
                    </Badge>
                  </div>
                  <div>
                    <p className="text-sm text-muted-foreground">Current Stage</p>
                    <p className="font-medium capitalize">{currentApplication.approver_stage}</p>
                  </div>
                </div>
              </CardContent>
            </Card>
          )}

          {/* Applications List */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center space-x-2">
                <FileText className="h-5 w-5" />
                <span>All Applications</span>
              </CardTitle>
              <CardDescription>Complete history of your leave applications</CardDescription>
            </CardHeader>
            <CardContent>
              {applications.length === 0 ? (
                <div className="text-center py-12">
                  <FileText className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
                  <h3 className="text-lg font-medium text-foreground mb-2">No Applications Yet</h3>
                  <p className="text-muted-foreground">You haven't submitted any leave applications.</p>
                </div>
              ) : (
                <div className="space-y-4">
                  {applications.map((app) => (
                    <Card key={app.id} className="hover:shadow-md transition-shadow" data-testid={`card-application-${app.id}`}>
                      <CardContent className="p-6">
                        <div className="flex items-start justify-between mb-4">
                          <div className="flex items-start space-x-3">
                            <div className="w-10 h-10 bg-primary/10 rounded-full flex items-center justify-center">
                              <FileText className="h-5 w-5 text-primary" />
                            </div>
                            <div>
                              <h3 className="font-semibold text-foreground">#{app.id?.slice(-6)}</h3>
                              <p className="text-sm text-muted-foreground capitalize">{app.leave_type} Leave</p>
                            </div>
                          </div>
                          <Badge variant={getStatusVariant(app.status)} className="flex items-center space-x-1">
                            {getStatusIcon(app.status)}
                            <span>{app.status}</span>
                          </Badge>
                        </div>

                        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-4">
                          <div className="flex items-center space-x-2">
                            <Calendar className="h-4 w-4 text-muted-foreground" />
                            <div>
                              <p className="text-sm text-muted-foreground">Date Range</p>
                              <p className="font-medium text-sm">
                                {new Date(app.start_date).toLocaleDateString()} - {new Date(app.end_date).toLocaleDateString()}
                              </p>
                            </div>
                          </div>
                          <div className="flex items-center space-x-2">
                            <Clock className="h-4 w-4 text-muted-foreground" />
                            <div>
                              <p className="text-sm text-muted-foreground">Current Stage</p>
                              <p className="font-medium text-sm capitalize">{app.approver_stage}</p>
                            </div>
                          </div>
                          <div className="flex items-center space-x-2">
                            <AlertCircle className="h-4 w-4 text-muted-foreground" />
                            <div>
                              <p className="text-sm text-muted-foreground">Guardian Contact</p>
                              <p className="font-medium text-sm">{app.guardian_phone}</p>
                            </div>
                          </div>
                        </div>

                        <div className="border-t pt-4">
                          <p className="text-sm text-muted-foreground mb-2">Reason for Leave</p>
                          <p className="text-sm text-foreground">{app.reason}</p>
                        </div>

                        {app.comments && (
                          <div className="border-t pt-4 mt-4">
                            <p className="text-sm text-muted-foreground mb-2">Comments</p>
                            <p className="text-sm text-foreground">{app.comments}</p>
                          </div>
                        )}

                        {app.status === "approved" && app.final_qr_url && (
                          <div className="border-t pt-4 mt-4">
                            <div className="flex items-start justify-between gap-6">
                              <div className="flex-1">
                                <div className="flex items-center space-x-2 mb-3">
                                  <QrCode className="h-5 w-5 text-primary" />
                                  <h4 className="font-semibold text-foreground">QR Leave Pass</h4>
                                </div>
                                <p className="text-sm text-muted-foreground mb-4">
                                  Your leave application has been approved. Use this QR code for verification.
                                </p>
                                <Button
                                  onClick={() => handleDownloadPDF(app.id)}
                                  variant="default"
                                  className="w-full sm:w-auto"
                                  data-testid={`button-download-pdf-${app.id}`}
                                >
                                  <Download className="w-4 h-4 mr-2" />
                                  Download PDF Pass
                                </Button>
                              </div>
                              <div className="flex-shrink-0">
                                <div className="w-32 h-32 bg-white border-2 border-border rounded-lg flex items-center justify-center p-2">
                                  <img 
                                    src={app.final_qr_url} 
                                    alt="QR Leave Pass" 
                                    className="w-full h-full object-contain"
                                    data-testid={`img-qr-code-${app.id}`}
                                  />
                                </div>
                              </div>
                            </div>
                          </div>
                        )}
                      </CardContent>
                    </Card>
                  ))}
                </div>
              )}
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}

