// Firebase Admin configuration for server-side
let db: FirebaseFirestore.Firestore | null = null;
let initPromise: Promise<FirebaseFirestore.Firestore | null> | null = null;

function tryInitializeFirestore(): Promise<FirebaseFirestore.Firestore | null> {
  if (initPromise) return initPromise;

  initPromise = (async () => {
    if (!getApps().length) {
      try {
        // Try to initialize with service account (production)
        let serviceAccount = null;
        if (process.env.FIREBASE_SERVICE_ACCOUNT_KEY) {
          try {
            // Clean control characters and malformed JSON before parsing
            let jsonString = process.env.FIREBASE_SERVICE_ACCOUNT_KEY;

            // Remove common control characters that cause parsing issues
            jsonString = jsonString
              .replace(/[\x00-\x08\x0B\x0C\x0E-\x1F\x7F]/g, '') // Remove control characters
              .replace(/\\"/g, '"') // Fix escaped quotes
              .replace(/\\\\/g, '\\') // Fix escaped backslashes
              .trim();

            console.log('Attempting to parse Firebase service account JSON...');
            serviceAccount = JSON.parse(jsonString);
            console.log('Successfully parsed Firebase service account JSON');
          } catch (jsonError: any) {
            console.error('Failed to parse Firebase service account JSON:', jsonError.message);
            console.error('JSON parsing error at position:', jsonError.message.match(/position (\d+)/)?.[1] || 'unknown');
            serviceAccount = null;
          }
        }

        if (serviceAccount && process.env.FIREBASE_PROJECT_ID) {
          // Fix private key formatting - handle various encoding issues
          if (serviceAccount.private_key) {
            let privateKey = serviceAccount.private_key;

            // First, handle double-escaped newlines that sometimes occur
            privateKey = privateKey.replace(/\\\\n/g, '\n');
            // Then handle single-escaped newlines
            privateKey = privateKey.replace(/\\n/g, '\n');

            // Remove any extra whitespace
            privateKey = privateKey.trim();

            // If the private key is all on one line, we need to format it properly
            if (!privateKey.includes('\n') && privateKey.includes('-----BEGIN PRIVATE KEY-----')) {
              // Split the key into proper PEM format with 64-character lines
              const header = '-----BEGIN PRIVATE KEY-----';
              const footer = '-----END PRIVATE KEY-----';

              // Extract just the key content between the header and footer
              const keyContent = privateKey
                .replace(header, '')
                .replace(footer, '')
                .replace(/\s/g, ''); // Remove all whitespace

              // Format key content into 64-character lines
              const formattedLines = [];
              for (let i = 0; i < keyContent.length; i += 64) {
                formattedLines.push(keyContent.slice(i, i + 64));
              }

              // Reconstruct the properly formatted key
              privateKey = header + '\n' + formattedLines.join('\n') + '\n' + footer + '\n';
              console.log('Reformatted private key to proper PEM format');
            }

            // Ensure proper PEM format with correct line endings
            if (!privateKey.startsWith('-----BEGIN PRIVATE KEY-----')) {
              console.warn('Private key does not start with proper PEM header');
            }

            // Ensure it ends properly
            if (!privateKey.endsWith('-----END PRIVATE KEY-----\n')) {
              if (privateKey.endsWith('-----END PRIVATE KEY-----')) {
                privateKey += '\n';
              } else {
                console.warn('Private key does not end with proper PEM footer');
              }
            }

            serviceAccount.private_key = privateKey;
            console.log('Private key formatting applied - length:', privateKey.length, 'lines:', privateKey.split('\n').length);
          }

          console.log('Attempting to initialize Firebase with service account...');
          initializeApp({
            credential: cert(serviceAccount),
            projectId: process.env.FIREBASE_PROJECT_ID,
          });
        } else if (process.env.FIREBASE_PROJECT_ID) {
          // Fallback for development with Application Default Credentials
          initializeApp({
            projectId: process.env.FIREBASE_PROJECT_ID,
          });
        } else {
          console.warn("No Firebase configuration found");
          return null;
        }

        const testDb = getFirestore();

        // Skip connectivity test for now due to decoder issues, but store the db instance
        console.log("Firebase Firestore initialized (skipping connectivity test)");
        db = testDb;
        return testDb;
      } catch (error) {
        console.error("Firebase Admin initialization failed:", error);
        return null;
      }
    } else {
      try {
        const testDb = getFirestore();
        // Skip connectivity test for existing app as well
        db = testDb;
        return testDb;
      } catch (error) {
        console.error("Firebase initialization failed:", error);
        return null;
      }
    }
  })();

  return initPromise;
}

async function ensureFirestore(): Promise<FirebaseFirestore.Firestore | null> {
  if (db) return db;
  return await tryInitializeFirestore();
}

export { ensureFirestore };

